/**
 * Relationen 1 - * (bidirektionale)
 * zwischen den Klassen ${primary_type_name}
 *   (RoleName ${ClientRoleName:var(java.lang.String)})
 * und ${supplierClassName:var(java.lang.String)}
 *   (RoleName ${SupplierRoleName:var(java.lang.String)} / ${supplierRoleName:var(java.lang.String)})
 * <br>
 */
@Indexing(IndexingType.INDEX)
private List<${supplierClassName}> relation${supplierRoleName} = new Vector<${supplierClassName}>();

/**
   * Fügt eine Referenz auf ${supplierClassName} hinzu.
   *
   * @param obj Referenz auf ${supplierClassName} die als Relation hinzugefügt wird.
   */
public void addRelation${SupplierRoleName}(
    final ${supplierClassName} obj)
  {
    if (obj == null)
    {
      throw new BoException("Null can't be added as a relationship reference "
        + "for ${className}.relation${supplierRoleName}.");
    }

    this.relation${supplierRoleName}.add(obj);

    //
    //Beziehung auf der Gegenseite (am ${supplierClassName}) zu dieser Klasse (${className})
    //mit Hilfe der set...OneWay-Methode pflegen
    //
    obj.setSourceOneWay(this);
  }
  
/**
   * TOPLink MANY_TO_ANYTHING: Wenn Attribut auf der Gegenseite navigierbar ist
   *
   * @param obj ${supplierClassName} wird hinzugefügt.
   */
  public void addRelation${SupplierRoleName}OneWay(
    ${supplierClassName} obj)
  {
    this.relation${supplierRoleName}.add(obj);
  }
  
 /**
   * Dient TOPLink dazu die <code>List</code> für lazy-loading der
   * "n zu"-Relation zu setzen.
   *
   * @param newList List für TOPLink lazy-loading
   */
  public void setRelation${SupplierRoleName}(final List<${supplierClassName}> newList)
  {
    this.relation${supplierRoleName} = newList;
  }

/**
   * Lieferte für TOPLink eine <code>List</code> der einer "n zu"-Relation.
   *
   * @return List<${supplierClassName}> für TOPLink lazy-loading
   */
  public List<${supplierClassName}> getRelation${SupplierRoleName}()
  {
    return this.relation${supplierRoleName};
  }

  /**
   * Gibt einen Iterator mit Objekten, zu denen eine Relation besteht, zurück.
   *
   * @return NIteratorOf${supplierClassName}
   */
  public NIterator<${supplierClassName}> getRelation${SupplierRoleName}Iterator()
  {
    NIterator<${supplierClassName}> iter = new NIterator<${supplierClassName}>(
      this.relation${supplierRoleName});

    iter.setTaskServerControllerInterface(getTaskServerControllerInterface());

    return iter;
  }

  /**
   * Gibt ein geklontes Set mit Objekten, zu denen die Relation Relation${SupplierRoleName} besteht, zurück.
   *
   * @return Set mit ${supplierClassName}
   */
  public NSet<${supplierClassName}> getRelation${SupplierRoleName}Set()
  {
    NSet<${supplierClassName}> nSet = new NSet<${supplierClassName}>();
    NIterator<${supplierClassName}> iter = getRelation${SupplierRoleName}Iterator();

    while (iter.hasNext())
    {
      nSet.addTo(iter.next());
    }

    return nSet;
  }

/**
   * Entfernt die Relation zu dem übergebenen Objekt.
   *
   * @param obj ${supplierClassName} des Relation entfernt werden soll.
   */
  public void removeRelation${SupplierRoleName}(
    final ${supplierClassName} obj)
  {
    if (obj == null)
    {
      throw new BoException("Null is not a reference which can be removed from"
        + " relationship ${className}.relation${supplierRoleName}.");
    }
    this.relation${supplierRoleName}.remove(obj);

    //
    //Beziehung auf der Gegenseite (am ${supplierClassName}) zu dieser Klasse (${className})
    //mit Hilfe der set...OneWay-Methode auf null setzen
    //
    obj.setSourceOneWay(null);
  }

  /**
   * TOPLink MANY_TO_ANYTHING: Wenn Attribut auf der Gegenseite navigierbar ist
   *
   * @param obj ${supplierClassName} wird entfernt.
   */
  public void removeRelation${SupplierRoleName}OneWay(
    ${supplierClassName} obj)
  {
    this.relation${supplierRoleName}.remove(obj);
