  {
    if (!istBestandswirksam())
    {
      throw new BesEntitaetRuntimeException(
        BesEntitaetRuntimeException.ERR_BESTANDSWIRKSAMER_ENTITAETSZUSTAND_FEHLT,
        new String[] { String.valueOf(getBoId()) });
    }

    if (getErstellungszeitpunkt() == null)
    {
      throw new BesGEDRuntimeException(
        BesGEDRuntimeException.ERR_PFLICHTATTRIBUT_FEHLT,
        new String[] { String.valueOf(getBoId()), "Erstellungszeitpunkt" });
    }
  }

