import org.eclipse.persistence.indirection.ValueHolder;
import org.eclipse.persistence.indirection.ValueHolderInterface;

import de.bkm.bes.basis.annotation.Indexing;
import de.bkm.bes.basis.annotation.IndexingType;
import de.bkm.bes.basis.annotation.Persistence;
import de.bkm.bes.basis.exceptions.BesUngueltigerParameterException;
import de.bkm.bes.basis.role.BoIEntitaetenrolle;
import de.bkm.bes.basis.role.BoIRollenfaehig;
import de.jdpe.bof.bo.eh.BoException;
import de.jdpe.bof.bo.pm.BoId;
import de.jdpe.bof.bo.pm.BoInterface;
import de.jdpe.bof.bo.pm.TaskServerControllerInterface;

/**
 * Abstrakte Rolle ${primary_type_name}
 * der Rollenfähigen Enität ${entitaet:var(java.lang.String)}.<br>
 * ${TODO}
 * <br>
 * @author ${user}
 * @version $$Log$$
 */
@Persistence(true)
public abstract class ${primary_type_name} implements BoIEntitaetenrolle
{

  /**
   * Eindeutige BoId des Business Objects.
   */
  private transient BoId boId;

  /**
   * Object-Nummer der BoId des Business Objects.
   * @see #boId
   */
  @Indexing(IndexingType.INDEX)
  private long objectNr;

  /**
   * Server-Nummer der BoId des Business Objects.
   * @see #boId
   */
  @Indexing(IndexingType.INDEX)
  private long serverNr;

  /**
   * TaskServerController der die UnitOfWork hält.
   */
  private transient TaskServerControllerInterface taskServerControllerInterface;

  /**
   * TOPLink interne Version des Business Objects.
   */
  @Indexing(IndexingType.NO_INDEX)
  private long version;

  /**
   * TopLink ValueHolder zur Rollenfähigen Entität.
   * ValueHolder zum Abbilden der Beziehung zu ${entitaet}
   * @associates ${entitaet}
   * @supplierRole RollenfaehigeEntitaet
   * @clientCardinality *
   * @supplierCardinality 1
   * @directed directed
   */
  @Indexing(IndexingType.INDEX)
  private ValueHolderInterface rollenfaehigeEntitaet = new ValueHolder();

  /**
   * Standard-Konstruktor für die Klasse ${primary_type_name}
   */
  public ${primary_type_name}()
  {
    // Nothing
  }

  /**
   * Konstruktor mit BoId für die Klasse ${primary_type_name}
   * @param aBoId BoId der Klasse
   */
  public ${primary_type_name}(BoId aBoId)
  {
    setBoId(aBoId);
  }

  /**
   * Löscht das Business Object aus der Datenbank.
   */
  @Override
  public void delete()
  {
    getTaskServerControllerInterface().deleteBo(this);
  }

  /**
   * Liefert die eindeutige BoId des Business Objects.
   *
   * @return eindeutige BoId des Business Objects
   */
  @Override
  public BoId getBoId()
  {
    if (this.boId == null)
    {
      this.boId = new BoId(
        this.serverNr, this.objectNr, this.getRoleName());
    }
    return this.boId;
  }

  /**
   * Liefert die Object-Nummer des Business Objects.
   *
   * @return Object-Nummer als long
   */
  @Override
  public long getObjectNr()
  {
    return this.objectNr;
  }

  /**
   * Liefert den RoleName der Klasse.
   *
   * @return RoleName der Klasse
   */
  @Override
  public String getRoleName()
  {
    return getClass().getName();
  }

  /**
   * Liefert die Server-Nummer des Business Objects.
   *
   * @return Server-Nummer als long
   */
  @Override
  public long getServerNr()
  {
    return this.serverNr;
  }

  /**
   * Gibt das TaskServerControllerInterface zurück.
   *
   * @return TaskServerController
   */
  @Override
  public TaskServerControllerInterface getTaskServerControllerInterface()
  {
    return this.taskServerControllerInterface;
  }

  /**
   * Liefert die Versionsnummer des Business Objects.
   *
   * @return Versionsnummer als long
   */
  public long getVersion()
  {
    return this.version;
  }

  /**
   * Initialisierung des Objektes bei Erzeugung
   */
  @Override
  public void init()
  {
    // Nothing
  }

  /**
   * Mit Hilfe dieser Methode setzt der Konstruktor die BoId.
   *
   * @param aBoId BoId für das Objekt
   */
  protected final void setBoId(final BoId aBoId)
  {
    if (!aBoId.getRoleName().equals(
      this.getRoleName()))
    {
      throw new BoException(
        "Rolename mismatch: "
          + this.getRoleName() + " != " + aBoId.getRoleName());
    }
    this.serverNr = aBoId.getServerNr();
    this.objectNr = aBoId.getObjectNr();
    this.boId = aBoId;
  }

  /**
   * Setzt die Server-Nummer des BusinessObjects.
   * <b>Diese Methode wird von TopLink benötigt, sie darf nicht benutzt werden!</b>
   *
   * @param aObjectNr Object-Nummer des BusinessObjects
   */
  public void setObjectNr(final long aObjectNr)
  {
    this.objectNr = aObjectNr;
  }

  /**
   * Setzt die Server-Nummer des BusinessObjects.
   * <b>Diese Methode wird von TopLink benötigt, sie darf nicht benutzt werden!</b>
   *
   * @param aServerNr Server-Nummer des BusinessObjects
   */
  public void setServerNr(final long aServerNr)
  {
    this.serverNr = aServerNr;
  }

  /**
   * Setzt den TaskServerController.
   *
   * @param aTsc TaskServerController der die UnitOfWork hält.
   */
  public void setTaskServerControllerInterface(final TaskServerControllerInterface aTsc)
  {
    this.taskServerControllerInterface = aTsc;
  }

  /**
   * Setzt die Versionsnummer des BusinessObjects.
   * <b>Diese Methode wird von TopLink benötigt, sie darf nicht benutzt werden!</b>
   *
   * @param aVersionNr Versionsnummer des BusinessObjects
   */
  public void setVersion(final long aVersionNr)
  {
    this.version = aVersionNr;
  }

  /**
   * @see java.lang.Object#equals(java.lang.Object)
   */
  @Override
  public boolean equals(final Object obj)
  {
    boolean result = false;
    if (this == obj)
    {
      result = true;
    }
    else if (obj instanceof ${primary_type_name})
    {
      result = getBoId().equals(
        ((${primary_type_name}) obj).getBoId());
    }
    return result;
  }
  
  /**
   * Liefert das Objekt, auf das sich die Relation bezieht.
   *
   * @return Objekt vom Typ ${entitaet},
   *         auf das die Relation verweist.
   */
  public ${entitaet} getRollenfaehigeEntitaet()
  {
   ${entitaet} obj = (${entitaet}) this.rollenfaehigeEntitaet.getValue();

    if (obj != null)
    {
      obj.setTaskServerControllerInterface(getTaskServerControllerInterface());
    }
    return obj;
  }

  /**
   * Legt fest auf welches Objekt sich die Relation bezieht.
   *
   * @param obj Das Objekt, vom Typ ${entitaet},
   *            zu dem eine Relation erzeugt werden soll.
   */
  public void setRollenfaehigeEntitaet(final ${entitaet} obj)
  {
    this.rollenfaehigeEntitaet.setValue(obj);
  }

  /**
   * Gibt den ValueHolder, bei einer 1:n Relation, für TOPLink zurück.
   *
   * @return ValueHolder für das lazy-loading von TOPLink
   */
  public ValueHolderInterface getRollenfaehigeEntitaetHolder()
  {
    ValueHolderInterface holder = this.rollenfaehigeEntitaet;

    if (holder.isInstantiated())
    {
      BoInterface bo = (BoInterface) holder.getValue();
      if (bo != null && !bo.getBoId().isValid())
      {
        holder.setValue(null);
      }
    }
    return holder;
  }

  /**
   * Setzt den ValueHolder, bei einer "1-zu" Relation, für TOPLink.
   *
   * @param valueHolder ValueHolder für das lazy-loading von TOPLink
   */
  public void setRollenfaehigeEntitaetHolder(final ValueHolderInterface valueHolder)
  {
    this.rollenfaehigeEntitaet = valueHolder;
  }
  
  /**
   * Liefert die rollenfähige Entität, die sich hinter der Rolle verbirgt.
   * 
   * @return BoIRollenfaehig
   * @see de.bkm.bes.basis.role.BoIEntitaetenrolle#gibRollenfaehigeEntitaet()
   */
  @Override
  public BoIRollenfaehig gibRollenfaehigeEntitaet()
  {
    return getRollenfaehigeEntitaet();
  }

  /**
   * Initialisiert die Rolle mit der Rollenfähigen Entität, 
   * welche sich hinter der Rolle verbergen soll.
   * 
   * @param aRollenfaehigeEntitaet BoIRollenfaehig
   * @see de.bkm.bes.basis.role.BoIEntitaetenrolle#init(de.bkm.bes.basis.role.BoIRollenfaehig)
   */
  @Override
  public void init(BoIRollenfaehig aRollenfaehigeEntitaet)
  {
    if (aRollenfaehigeEntitaet == null)
    {
      throw new BesUngueltigerParameterException(
        BesUngueltigerParameterException.ERR_PARAMETER_UNGUELTIG, 
        new String[] {
          "aRollenfaehigeEntitaet", null });
    }
    setRollenfaehigeEntitaet((${entitaet}) aRollenfaehigeEntitaet);
  }

  /**
   * Trennt die Relation zwischen Rolle und rollenfähiger Entität.
   * 
   * @see de.bkm.bes.basis.role.BoIEntitaetenrolle#loescheEntitaet()
   */
  @Override
  public void loescheEntitaet()
  {
    setRollenfaehigeEntitaet(null);
  }
}

/*
 * $$Log$$
 */
