/**
 * Enum für das ${todo}.<br>
 * <br>
 * @author ${user}
 * @version $$Log$$
 */
public enum ${primary_type_name} implements IEnumGanzzahl
{
  ${cursor}

  ;

 /**
   * Wert des Schlüssel/Wert-Paars. Beschreibung des Schlüssels.<BR>
   * <BR>   
   */
  private String beschreibung = null;

  /**
   * Schlüssel des Schlüssel/Wert-Paars.<BR>
   * <BR>   
   */
  private int schluessel = 0;

  /**
   * Privater Konstruktor der Klasse ${primary_type_name}
   * @param aSchluessel Schlüssel des Enums
   * @param aBeschreibung Beschreibung des Enums
   *
   */
  private ${primary_type_name}(final int aSchluessel, final String aBeschreibung)
  {
    this.schluessel = aSchluessel;
    this.beschreibung = aBeschreibung;
  }
  
  /**
   * Liefert die Enumausprägung zur übergebenen Beschreibung.
   * @param aBeschreibung Beschreibung des gesuchten Enums.
   * @return Enum zur übergebenen Beschreibung bzw. <code>null</code>,
   * wenn kein entsprechender Enum gefunden wurde.
   */
  public static ${primary_type_name} gibEnumZuBeschreibung(final String aBeschreibung)
  {
    ${primary_type_name} result = null;
    ${primary_type_name}[] alleEnums = values();

    for (int i = 0; i < alleEnums.length; i++)
    {
      if (alleEnums[i].gibBeschreibung().equals(aBeschreibung))
      {
        result = alleEnums[i];
        break;
      }
    }

    return result;
  }

  /**
   * Liefert die Enumausprägung zum übergebenen Schlüssel.<BR>
   * <BR>
   * @param aSchluessel int
   * @return EnumAuszahlungsart
   */
  public static ${primary_type_name} gibEnumZuSchluessel(final int aSchluessel)
  {
    ${primary_type_name} result = null;
    ${primary_type_name}[] alleEnums = values();

    for (int i = 0; i < alleEnums.length; i++)
    {
      if (aSchluessel == alleEnums[i].gibSchluessel())
      {
        result = alleEnums[i];
        break;
      }
    }
    return result;
  }
  
  /**
   * Liefert die Beschreibung der enum-Ausprägung.<BR>
   * 
   * @return String
   */
  @Override
  public String gibBeschreibung()
  {
    return this.beschreibung;
  }

  /**
   * Liefert den Schlüssel der enum-Ausprägung.<BR>
   * 
   * @return int
   */
  @Override
  public int gibSchluessel()
  {
    return this.schluessel;
  }

  /**
   * Überschreibt die toString()-Methode.<BR>
   * Liefert eine Kombination aus Schlüssel und Beschreibung zurück.<BR>
   * 
   * @return String
   */
  @Override
  public String toString()
  {
    return "<" + gibSchluessel() + ", " + gibBeschreibung() + ">";
  }
}

